import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  NavLink
} from 'react-router-dom';
import './App.css';
import HomeButton from './HomeButton.js'

const activeNav = {color: 'grey', borderStyle: 'solid', borderColor: '#dee2e6 #dee2e6 #fff'}
const inactiveNav = {padding: '5px', textDecoration: 'none', cursor: 'default', 
  border: '1px solid transparent', borderTopLeftRadius: '.25rem', borderTopRightRadius: '.25rem'}


function App() {
  return(
    <div>
      <div class="navbar">
        <NavLink exact to="/" style={inactiveNav} activeStyle={activeNav}>Home</NavLink>
        <NavLink to="/about" style={inactiveNav} activeStyle={activeNav}>About</NavLink>
        <NavLink to="/user" style={inactiveNav} activeStyle={activeNav}>Users</NavLink>
      </div>
      <div style={{fontSize: '3em', margin: '25px'}}>
        <Switch>
          <Route exact path="/" component={Home}></Route>
          <Route exact path="/about" component={About}></Route>
          <Route exact path="/user/" component={Users}></Route>
          <Route path="/user/:name" component={User}></Route>
          <Route path="/" component={PageNotFound}></Route>
        </Switch>
      </div>
      <HomeButton/>
    </div>
  );  
}

function Home() {
  return(
    <h2>Welcome to this page</h2>
  )
}

function About() {
  return(
    <h2>About</h2>
  );
}

function Users() {
  return(
    <h2>Users</h2>
  );
}

function User({match}) {
  return(
    <div>
      <h2>Users</h2>
      <div>Hello, {match.params.name}</div>
    </div>
  );
}

function PageNotFound() {
  return(
    <h2>404 Page Not Found</h2>
  )
}

export default App;
