import React from 'react';
import {
  Switch,
  Route,
  NavLink
} from 'react-router-dom';
import './App.css';

const activeNav = {color: 'grey', borderColor: '#dee2e6 #dee2e6 #fff'}
const inactiveNav = {padding: '.5rem 3rem', textDecoration: 'none', cursor: 'default', 
  border: '1px solid transparent', borderTopLeftRadius: '.25rem', borderTopRightRadius: '.25rem'}


function App() {
  return(
    <div>
      <div class="navbar">
        <NavLink activeClassName='nav-item-active' exact to="/" >Bio</NavLink>
        <NavLink activeClassName='nav-item-active' to="/education">Education</NavLink>
        <NavLink activeClassName='nav-item-active' to="/experience">Experience</NavLink>
      </div>
      <div style={{fontSize: '3em', margin: '25px'}}>
        <Switch>
          <Route exact path="/">
            <Intro />
          </Route>
          <Route exact path="/education" component={Education}></Route>
          <Route exact path="/experience/" component={Experience}></Route>
          <Route path="/experience/:name" component={User}></Route>
          <Route path="/" component={PageNotFound}></Route>
        </Switch>
      </div>
    </div>
  );  
}

function Intro() {
  return(
    <h2>Welcome to this page</h2>
  )
}

function Education() {
  return(
    <h2>Education</h2>
  );
}

function Experience() {
  return(
    <h2>Experience</h2>
  );
}

function User({match}) {
  return(
    <div>
      <h2>Experience</h2>
      <div>Hello, {match.params.name}</div>
    </div>
  );
}

function PageNotFound() {
  return(
    <h2>404 Page Not Found</h2>
  )
}

export default App;
